import React, { Component } from "react";
import "./Card.css";
class Card extends Component {
  constructor(props) {
    super(props);
    this.state = {
      flipped: this.props.flipped,
      id: this.props.id
    };
    this.toggle = () => {
      console.log("clicked");
      this.setState({ flipped: !this.state.flipped });
    };
  }
  render() {
    if (this.flipped) {
      return (
        <div className="Card" onClick={this.toggle}>
          {this.props.emoji}
        </div>
      );
    } else {
      return <div className="Card" onClick={this.toggle} />;
    }
  }
}

export default Card;
