import React from "react";
import "./Card.css";
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      flipped: false
    };
    this.toggle = () => {
      this.setState({ flipped: !this.state.flipped });
    };
  }
  render() {
    return (
      <div className="Card" onClick={this.props.toggle}>
        {this.props.emoji}
      </div>
    );
  }
}
export default Card;
